import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';

function AdminProductsPage() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    stock_quantity: 0,
  });
  const token = localStorage.getItem('token');

  const fetchProducts = async () => {
    try {
      const res = await axios.get('/products');
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const addProduct = async () => {
    try {
      await axios.post('/products', newProduct, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts();
      setNewProduct({ name: '', price: '', stock_quantity: 0 });
    } catch (err) {
      console.error(err);
    }
  };

  const deleteProduct = async (id) => {
    try {
      await axios.delete(`/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Gestionare Produse (Admin)</h2>

      {/* Adăugare Produs */}
      <div>
        <input
          type="text"
          placeholder="Nume"
          value={newProduct.name}
          onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Preț"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
        />
        <input
          type="number"
          placeholder="Stoc"
          value={newProduct.stock_quantity}
          onChange={(e) => setNewProduct({ ...newProduct, stock_quantity: e.target.value })}
        />
        <button onClick={addProduct}>Adaugă Produs</button>
      </div>

      {/* Afișare Produse */}
      <ul>
        {products.map((prod) => (
          <li key={prod.id}>
            {prod.name} - {prod.price} USD | Stoc: {prod.stock_quantity}
            <button onClick={() => deleteProduct(prod.id)} style={{ marginLeft: '10px' }}>
              Șterge
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminProductsPage;
