import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]); // Starea pentru produsele din coș
  const [loading, setLoading] = useState(true);   // Starea pentru încărcare
  const [error, setError] = useState('');         // Starea pentru erori

  // Funcție pentru obținerea produselor din coș
  const fetchCartItems = async () => {
    try {
      const token = localStorage.getItem('token'); // JWT din localStorage
      const response = await axios.get('http://localhost:3001/api/cart/', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCartItems(response.data); // Actualizează starea cu produsele primite
      setLoading(false);
    } catch (err) {
      setError('Eroare la încărcarea produselor din coș.');
      setLoading(false);
    }
  };

  // Funcție pentru ștergerea unui produs din coș
  const handleRemoveFromCart = async (id) => {
    try {
      const token = localStorage.getItem('token'); // JWT din localStorage
      await axios.delete(`http://localhost:3001/api/cart/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      // Actualizează lista locală de produse după ștergere
      setCartItems(cartItems.filter((item) => item.id !== id));
    } catch (err) {
      console.error('Eroare la ștergerea produsului din coș:', err);
      setError('Eroare la ștergerea produsului din coș.');
    }
  };

  // Încarcă produsele la montarea componentei
  useEffect(() => {
    fetchCartItems();
  }, []);

  // Returnează componentele UI
  if (loading) return <p>Se încarcă...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div>
      <h1>Coșul de Cumpărături</h1>
      {cartItems.length === 0 ? (
        <p>Coșul este gol.</p>
      ) : (
        <ul>
          {cartItems.map((item) => (
            <li key={item.id} style={{ marginBottom: '10px' }}>
              <strong>{item.Product.name}</strong> - {item.Product.price} USD - Cantitate: {item.quantity}
              <button
                onClick={() => handleRemoveFromCart(item.id)}
                style={{
                  marginLeft: '10px',
                  backgroundColor: 'red',
                  color: 'white',
                  border: 'none',
                  padding: '5px 10px',
                  cursor: 'pointer',
                }}
              >
                Șterge
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Cart;
