import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';

function AdminProductsPage() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    size: '',
    image_url: '',
    stock_quantity: '',
  });
  const [editingProduct, setEditingProduct] = useState(null); // Produsul pentru editare
  const [error, setError] = useState('');

  const token = localStorage.getItem('token'); // JWT pentru autentificare

  // Fetch produse existente
  const fetchProducts = async () => {
    try {
      const response = await axios.get('/products');
      setProducts(response.data);
    } catch (err) {
      console.error(err);
      setError('Eroare la încărcarea produselor.');
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Funcție pentru adăugarea unui produs nou
  const handleAddProduct = async () => {
    try {
      await axios.post('/products', newProduct, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts(); // Reîncarcă lista produselor
      setNewProduct({ name: '', price: '', size: '', image_url: '', stock_quantity: '' });
    } catch (err) {
      console.error(err);
      setError('Eroare la adăugarea produsului.');
    }
  };

  // Funcție pentru actualizarea unui produs
  const handleUpdateProduct = async () => {
    try {
      await axios.put(`/products/${editingProduct.id}`, editingProduct, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts();
      setEditingProduct(null); // Oprește modul edit
    } catch (err) {
      console.error(err);
      setError('Eroare la actualizarea produsului.');
    }
  };

  // Funcție pentru ștergerea unui produs
  const handleDeleteProduct = async (id) => {
    try {
      await axios.delete(`/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts();
    } catch (err) {
      console.error(err);
      setError('Eroare la ștergerea produsului.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Gestionare Produse (Admin)</h2>

      {/* Adăugare Produs Nou */}
      <div>
        <h3>Adaugă Produs Nou</h3>
        <input
          type="text"
          placeholder="Nume"
          value={newProduct.name}
          onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Preț"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
        />
        <input
          type="text"
          placeholder="Mărime"
          value={newProduct.size}
          onChange={(e) => setNewProduct({ ...newProduct, size: e.target.value })}
        />
        <input
          type="number"
          placeholder="Stoc"
          value={newProduct.stock_quantity}
          onChange={(e) => setNewProduct({ ...newProduct, stock_quantity: e.target.value })}
        />
        <button onClick={handleAddProduct}>Adaugă Produs</button>
      </div>

      {/* Lista de Produse */}
      <h3>Produse</h3>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <ul>
        {products.map((prod) => (
          <li key={prod.id}>
            {editingProduct && editingProduct.id === prod.id ? (
              <div>
                {/* Editare Produs */}
                <input
                  type="text"
                  value={editingProduct.name}
                  onChange={(e) => setEditingProduct({ ...editingProduct, name: e.target.value })}
                />
                <input
                  type="number"
                  value={editingProduct.price}
                  onChange={(e) => setEditingProduct({ ...editingProduct, price: e.target.value })}
                />
                <button onClick={handleUpdateProduct}>Salvează</button>
                <button onClick={() => setEditingProduct(null)}>Anulează</button>
              </div>
            ) : (
              <div>
                <strong>{prod.name}</strong> - {prod.price} USD - Stoc: {prod.stock_quantity}
                <button onClick={() => setEditingProduct(prod)}>Editează</button>
                <button onClick={() => handleDeleteProduct(prod.id)} style={{ marginLeft: '10px', color: 'red' }}>
                  Șterge
                </button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminProductsPage;
