import React, { useState } from 'react';
import axios from '../api/axiosInstance';
import { useNavigate } from 'react-router-dom';

function RegisterPage() {
  const [username, setUsername] = useState('');
  const [email, setEmail]   = useState('');
  const [password, setPassword] = useState('');
  const [error, setError]   = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');

    try {
      const response = await axios.post('/users/register', { username, email, password });
      if (response.data.message) {
        setMessage('Înregistrare reușită! Te poți loga acum.');
      }
    } catch (err) {
      console.error(err);
      if (err.response && err.response.data && err.response.data.error) {
        setError(err.response.data.error);
      } else {
        setError('Eroare la înregistrare.');
      }
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Înregistrare</h2>
      <form onSubmit={handleRegister}>
        <div>
          <label>Username: </label>
          <input 
            type="text" 
            value={username}
            onChange={e => setUsername(e.target.value)} 
            required
          />
        </div>
        <div style={{ marginTop: '10px' }}>
          <label>Email: </label>
          <input 
            type="email" 
            value={email}
            onChange={e => setEmail(e.target.value)} 
            required
          />
        </div>
        <div style={{ marginTop: '10px' }}>
          <label>Parola: </label>
          <input 
            type="password" 
            value={password}
            onChange={e => setPassword(e.target.value)} 
            required
          />
        </div>
        {error && <div style={{color: 'red', marginTop: '10px'}}>{error}</div>}
        {message && <div style={{color: 'green', marginTop: '10px'}}>{message}</div>}
        <button type="submit" style={{ marginTop: '10px' }}>Înregistrează-te</button>
      </form>
      <button onClick={() => navigate('/login')} style={{ marginTop: '10px' }}>Mergi la Login</button>
    </div>
  );
}

export default RegisterPage;
