import React, { useState, useEffect } from 'react';
import axios from '../api/axiosInstance';

function AdminProductsPage() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    stock_quantity: '',
    image_url: ''
  });

  const token = localStorage.getItem('token');

  const fetchProducts = async () => {
    try {
      const response = await axios.get('/products');
      setProducts(response.data);
    } catch (err) {
      console.error('Eroare la încărcarea produselor:', err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleAddProduct = async () => {
    try {
      await axios.post('/products', newProduct, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts();
      setNewProduct({ name: '', price: '', stock_quantity: '', image_url: '' });
    } catch (err) {
      console.error('Eroare la adăugarea produsului:', err);
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      await axios.delete(`/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts();
    } catch (err) {
      console.error('Eroare la ștergerea produsului:', err);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Gestionare Produse (Admin)</h2>

      {/* Adăugare Produs Nou */}
      <div>
        <h3>Adaugă Produs Nou</h3>
        <input
          type="text"
          placeholder="Nume"
          value={newProduct.name}
          onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Preț"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
        />
        <input
          type="number"
          placeholder="Stoc"
          value={newProduct.stock_quantity}
          onChange={(e) => setNewProduct({ ...newProduct, stock_quantity: e.target.value })}
        />
        <input
          type="text"
          placeholder="Imagine URL"
          value={newProduct.image_url}
          onChange={(e) => setNewProduct({ ...newProduct, image_url: e.target.value })}
        />
        <button onClick={handleAddProduct}>Adaugă Produs</button>
      </div>

      {/* Afișare Produse */}
      <h3>Produse</h3>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            {product.name} - {product.price} USD - Stoc: {product.stock_quantity}
            <button onClick={() => handleDeleteProduct(product.id)} style={{ marginLeft: '10px', color: 'red' }}>
              Șterge
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminProductsPage;
