// src/pages/OrdersPage.jsx

import React, { useState, useEffect } from 'react';
import axios from '../api/axiosInstance'; // Asigură-te că axiosInstance este corect configurat

function OrdersPage() {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');

  // Funcție pentru a obține comenzile utilizatorului
  const fetchOrders = async () => {
    if (!token) {
      setError('Trebuie să fii logat pentru a vizualiza comenzile.');
      return;
    }

    try {
      const response = await axios.get('/orders', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setOrders(response.data);
    } catch (err) {
      console.error(err);
      setError('Eroare la obținerea comenzilor.');
    }
  };

  useEffect(() => {
    fetchOrders();
  }, [token]);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Istoricul comenzilor</h2>
      {error && <div style={{ color: 'red' }}>{error}</div>}

      {orders.length === 0 ? (
        <p>Nu ai nicio comandă încă.</p>
      ) : (
        <ul>
          {orders.map((order) => (
            <li key={order.id} style={{ marginBottom: '20px' }}>
              <h3>Comanda #{order.id}</h3>
              <p>Status: {order.status}</p>
              <p>Total: {order.total_price} USD</p>
              <ul>
                {order.OrderDetails.map((orderDetail) => (
                  <li key={orderDetail.id}>
                    {orderDetail.Product.name} - {orderDetail.quantity} x {orderDetail.price_per_unit} USD
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default OrdersPage;
