import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';

function ProductsPage() {
  const [products, setProducts] = useState([]);
  const [currency, setCurrency] = useState('USD');
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    size: '',
    image_url: '',
    stock_quantity: 0,
  });

  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role'); // Rolul utilizatorului

  const fetchProducts = async () => {
    try {
      let url = '/products';
      if (currency !== 'USD') {
        url = `/currency/${currency}`;
      }
      const res = await axios.get(url);
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [currency]);

  // Funcție pentru adăugarea produsului în coș (doar pentru clienți)
  const addToCart = async (productId) => {
    if (!token) {
      alert('Trebuie să fii logat pentru a adăuga în coș!');
      return;
    }
    try {
      await axios.post(
        '/cart',
        { product_id: productId, quantity: 1 },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Produs adăugat în coș!');
    } catch (err) {
      console.error(err);
      alert('Eroare la adăugarea în coș.');
    }
  };

  // Admin: Adaugă un produs nou
  const addProduct = async () => {
    try {
      await axios.post('/products', newProduct, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts();
      setNewProduct({ name: '', price: '', size: '', image_url: '', stock_quantity: 0 });
      alert('Produs adăugat cu succes!');
    } catch (err) {
      console.error(err);
      alert('Eroare la adăugarea produsului.');
    }
  };

  // Admin: Șterge un produs
  const deleteProduct = async (id) => {
    try {
      await axios.delete(`/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts();
      alert('Produs șters cu succes!');
    } catch (err) {
      console.error(err);
      alert('Eroare la ștergerea produsului.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Produse</h2>
      <div>
        <label>Alege moneda: </label>
        <select value={currency} onChange={(e) => setCurrency(e.target.value)}>
          <option value="USD">USD</option>
          <option value="EUR">EUR</option>
        </select>
      </div>

      {/* Formular pentru admin - Adăugare produs nou */}
      {role === 'admin' && (
        <div style={{ margin: '20px 0' }}>
          <h3>Adaugă Produs Nou</h3>
          <input
            type="text"
            placeholder="Nume"
            value={newProduct.name}
            onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
          />
          <input
            type="number"
            placeholder="Preț"
            value={newProduct.price}
            onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
          />
          <input
            type="text"
            placeholder="Mărime"
            value={newProduct.size}
            onChange={(e) => setNewProduct({ ...newProduct, size: e.target.value })}
          />
          <input
            type="text"
            placeholder="Imagine URL"
            value={newProduct.image_url}
            onChange={(e) => setNewProduct({ ...newProduct, image_url: e.target.value })}
          />
          <input
            type="number"
            placeholder="Stoc"
            value={newProduct.stock_quantity}
            onChange={(e) => setNewProduct({ ...newProduct, stock_quantity: e.target.value })}
          />
          <button onClick={addProduct} style={{ marginLeft: '10px', cursor: 'pointer' }}>
            Adaugă Produs
          </button>
        </div>
      )}

      {/* Afișarea produselor */}
      <ul>
        {products.map((prod) => (
          <li key={prod.id} style={{ marginBottom: '10px' }}>
            <strong>{prod.name}</strong> -{' '}
            {currency === 'USD' ? prod.price : prod.price_converted} {currency} |{' '}
            <span style={{ color: prod.stock_quantity > 0 ? 'green' : 'red' }}>
              Stoc: {prod.stock_quantity > 0 ? prod.stock_quantity : 'Stoc epuizat'}
            </span>
            {role === 'client' && token && prod.stock_quantity > 0 && (
              <button
                onClick={() => addToCart(prod.id)}
                style={{
                  marginLeft: '10px',
                  backgroundColor: 'blue',
                  color: 'white',
                  border: 'none',
                  padding: '5px 10px',
                  cursor: 'pointer',
                }}
              >
                Adaugă în coș
              </button>
            )}
            {role === 'admin' && (
              <>
                <button
                  onClick={() => deleteProduct(prod.id)}
                  style={{
                    marginLeft: '10px',
                    backgroundColor: 'red',
                    color: 'white',
                    border: 'none',
                    padding: '5px 10px',
                    cursor: 'pointer',
                  }}
                >
                  Șterge
                </button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductsPage;
