import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';

function ProductsPage() {
  const [products, setProducts] = useState([]);
  const [currency, setCurrency] = useState('USD');
  const [ronRate, setRonRate] = useState(1); // Rata de conversie pentru RON

  // Fetch produse din API
  const fetchProducts = async () => {
    try {
      let url = '/products';
      if (currency !== 'USD') {
        url = `/currency/${currency}`; // API-ul pentru conversie valutara
      }
      const res = await axios.get(url);
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Obține rata de conversie pentru RON
  const fetchRonRate = async () => {
    try {
      const response = await axios.get(`/currency/RON`);
      setRonRate(response.data.conversionRate); // Actualizează rata de conversie pentru RON
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchProducts();
    if (currency === 'RON') {
      fetchRonRate(); // Dacă moneda aleasă este RON, fetch rata de conversie
    }
  }, [currency]);

  const token = localStorage.getItem('token'); // Verificăm dacă utilizatorul este logat

  // Adăugare produs în coș
  const addToCart = async (productId) => {
    if (!token) {
      alert('Trebuie să fii logat pentru a adăuga în coș!');
      return;
    }
    try {
      await axios.post(
        '/cart',
        { product_id: productId, quantity: 1 },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Produs adăugat în coș!');
    } catch (err) {
      console.error(err);
      alert('Eroare la adăugarea în coș.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2 style={{ textAlign: 'center' }}>Produse</h2>

      <div style={{ marginBottom: '20px', textAlign: 'center' }}>
        <label>Alege moneda: </label>
        <select value={currency} onChange={(e) => setCurrency(e.target.value)} style={{ padding: '5px', marginLeft: '10px' }}>
          <option value="USD">USD</option>
          <option value="EUR">EUR</option>
          <option value="RON">RON</option> {/* Adăugăm RON */}
        </select>
      </div>

      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between' }}>
        {products.map((prod) => {
          // Conversie pentru RON
          const priceInRON = currency === 'RON' ? (prod.price * ronRate).toFixed(2) : prod.price_converted;

          return (
            <div key={prod.id} style={{ width: '200px', margin: '10px', padding: '15px', border: '1px solid #ddd', borderRadius: '8px', textAlign: 'center' }}>
              <img src={prod.image_url || 'https://via.placeholder.com/150'} alt={prod.name} style={{ width: '100%', height: 'auto', borderRadius: '8px' }} />
              <h3>{prod.name}</h3>
              <p>
                {currency === 'USD' ? prod.price : currency === 'EUR' ? prod.price_converted : priceInRON} {currency}
              </p>
              <p style={{ color: prod.stock_quantity > 0 ? 'green' : 'red' }}>
                Stoc: {prod.stock_quantity > 0 ? prod.stock_quantity : 'Stoc epuizat'}
              </p>
              {token && prod.stock_quantity > 0 && (
                <button
                  onClick={() => addToCart(prod.id)}
                  style={{
                    marginTop: '10px',
                    backgroundColor: 'blue',
                    color: 'white',
                    border: 'none',
                    padding: '10px 20px',
                    cursor: 'pointer',
                    borderRadius: '5px',
                  }}
                >
                  Adaugă în coș
                </button>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default ProductsPage;
