import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import ProductsPage from './components/ProductsPage';
import Cart from './components/Cart';
import Login from './components/Login';
import Register from './components/Register';
import AdminProductsPage from './components/AdminProductsPage';

function App() {
  const token = localStorage.getItem('token'); // Token-ul JWT
  const role = localStorage.getItem('role');   // Rolul utilizatorului ('admin' sau 'client')

  return (
    <Router>
      <Navbar />
      <Routes>
        {/* Pagina principală: Afișează produsele */}
        <Route path="/" element={<ProductsPage />} />

        {/* Pagina Coș */}
        {token ? (
          <Route path="/cart" element={<Cart />} />
        ) : (
          <Route path="/cart" element={<Navigate to="/login" />} />
        )}

        {/* Pagina Login */}
        <Route path="/login" element={<Login />} />

        {/* Pagina Register */}
        <Route path="/register" element={<Register />} />

        {/* Pagina Admin pentru gestionare produse */}
        {token && role === 'admin' ? (
          <Route path="/admin" element={<AdminProductsPage />} />
        ) : (
          <Route path="/admin" element={<Navigate to="/" />} />
        )}

        {/* Pagină 404 */}
        <Route path="*" element={<h2>404 - Pagina nu există</h2>} />
      </Routes>
    </Router>
  );
}

export default App;
