import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';

function ProductsPage() {
  const [products, setProducts] = useState([]);
  const [currency, setCurrency] = useState('USD');

  const fetchProducts = async () => {
    try {
      let url = '/products';
      if (currency !== 'USD') {
        url = `/currency/${currency}`;
      }
      const res = await axios.get(url);
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchProducts();
    // eslint-disable-next-line
  }, [currency]);

  const token = localStorage.getItem('token');

  const addToCart = async (productId) => {
    if (!token) {
      alert('Trebuie să fii logat pentru a adăuga în coș!');
      return;
    }
    try {
      await axios.post('/cart', 
        { product_id: productId, quantity: 1 },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Produs adăugat în coș!');
    } catch (err) {
      console.error(err);
      alert('Eroare la adăugarea în coș.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Produse</h2>
      <div>
        <label>Alege moneda: </label>
        <select value={currency} onChange={e => setCurrency(e.target.value)}>
          <option value="USD">USD</option>
          <option value="EUR">EUR</option>
        </select>
      </div>
      <ul>
        {products.map(prod => (
          <li key={prod.id}>
            {prod.name} - {currency === 'USD' ? prod.price : prod.price_converted} {currency}
            {token && <button onClick={() => addToCart(prod.id)}>Adaugă în coș</button>}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductsPage;
