import React, { useEffect, useState } from 'react';
import axios from '../api/axiosInstance';
import { useNavigate } from 'react-router-dom';

function CartPage() {
  const [items, setItems] = useState([]);
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      alert('Trebuie să fii logat pentru a vedea coșul!');
      navigate('/login');
      return;
    }
    axios.get('/cart', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(res => setItems(res.data))
    .catch(err => console.error(err));
    // eslint-disable-next-line
  }, []);

  const placeOrder = async () => {
    try {
      const res = await axios.post('/orders', {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert(`Comanda a fost plasată cu succes! ID: ${res.data.order_id}`);
      navigate('/products');
    } catch (err) {
      console.error(err);
      alert('Eroare la plasarea comenzii.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Coș de cumpărături</h2>
      {items.length === 0 ? (
        <p>Coșul este gol</p>
      ) : (
        <>
          <ul>
            {items.map(item => (
              <li key={item.id}>
                {item.Product.name} x {item.quantity} = {item.Product.price * item.quantity} USD
              </li>
            ))}
          </ul>
          <button onClick={placeOrder}>Plasează Comanda</button>
        </>
      )}
    </div>
  );
}

export default CartPage;
