import React, { useState, useEffect } from 'react';
import axios from '../api/axiosInstance';

function AdminProductsPage() {
  const [products, setProducts] = useState([]); // Lista produselor
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    size: '',
    image_url: '',
    stock_quantity: 0,
  });
  const [editingProduct, setEditingProduct] = useState(null); // Produsul pentru editare
  const [error, setError] = useState('');

  const token = localStorage.getItem('token'); // Token JWT pentru autentificare

  // Fetch produse din backend
  const fetchProducts = async () => {
    try {
      const response = await axios.get('/products');
      setProducts(response.data);
    } catch (err) {
      console.error('Eroare la încărcarea produselor:', err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Funcție pentru adăugare produs nou
  const handleAddProduct = async () => {
    try {
      await axios.post('/products', newProduct, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts();
      setNewProduct({ name: '', price: '', size: '', image_url: '', stock_quantity: 0 });
    } catch (err) {
      console.error('Eroare la adăugarea produsului:', err);
      setError('Eroare la adăugarea produsului.');
    }
  };

  // Funcție pentru actualizarea unui produs
  const handleUpdateProduct = async () => {
    try {
      await axios.put(`/products/${editingProduct.id}`, editingProduct, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts();
      setEditingProduct(null); // Ieșim din modul editare
    } catch (err) {
      console.error('Eroare la actualizarea produsului:', err);
      setError('Eroare la actualizarea produsului.');
    }
  };

  // Funcție pentru ștergerea unui produs
  const handleDeleteProduct = async (id) => {
    try {
      await axios.delete(`/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts();
    } catch (err) {
      console.error('Eroare la ștergerea produsului:', err);
      setError('Eroare la ștergerea produsului.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Gestionare Produse (Admin)</h2>

      {/* Adăugare Produs Nou */}
      <div>
        <h3>Adaugă Produs Nou</h3>
        <input
          type="text"
          placeholder="Nume"
          value={newProduct.name}
          onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Preț"
          value={newProduct.price}
          onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
        />
        <input
          type="text"
          placeholder="Mărime"
          value={newProduct.size}
          onChange={(e) => setNewProduct({ ...newProduct, size: e.target.value })}
        />
        <input
          type="text"
          placeholder="Imagine URL"
          value={newProduct.image_url}
          onChange={(e) => setNewProduct({ ...newProduct, image_url: e.target.value })}
        />
        <input
          type="number"
          placeholder="Stoc"
          value={newProduct.stock_quantity}
          onChange={(e) => setNewProduct({ ...newProduct, stock_quantity: e.target.value })}
        />
        <button onClick={handleAddProduct}>Adaugă Produs</button>
      </div>

      {/* Afișare Produse */}
      <h3>Lista Produselor</h3>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            {editingProduct && editingProduct.id === product.id ? (
              <div>
                <input
                  type="text"
                  value={editingProduct.name}
                  onChange={(e) => setEditingProduct({ ...editingProduct, name: e.target.value })}
                />
                <input
                  type="number"
                  value={editingProduct.price}
                  onChange={(e) => setEditingProduct({ ...editingProduct, price: e.target.value })}
                />
                <button onClick={handleUpdateProduct}>Salvează</button>
                <button onClick={() => setEditingProduct(null)}>Anulează</button>
              </div>
            ) : (
              <div>
                {product.name} - {product.price} USD - Stoc: {product.stock_quantity}
                <button onClick={() => setEditingProduct(product)} style={{ marginLeft: '10px' }}>
                  Editează
                </button>
                <button
                  onClick={() => handleDeleteProduct(product.id)}
                  style={{ marginLeft: '10px', color: 'red' }}
                >
                  Șterge
                </button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminProductsPage;
